import React, { useState } from 'react'
import firebase from "firebase";
import { useEffect } from "react";
import { useCallback } from "react";
import Card from '@mui/material/Card';
import CardHeader from '@mui/material/CardHeader';
import { CardActionArea } from '@mui/material';
import { storage } from '../index'
import { makeStyles } from '@material-ui/core/styles'
import Profile from '../Images/profilePic.JPG';
import axios from "axios"
import {
    Grid
  } from '@material-ui/core/'

  const useStyles = makeStyles((theme) => ({

    card:{
       borderStyle:"solid",
       borderColor:"#b291f2",
       width:"100%",
       height:"100%",

    },
    cardHeader:{
    background: "#e2d7f7",
    color:"white"

   },
   blogs:{
    position: "absolute",

    left: "50%",
    transform: "translate(-50%, -50%)",
    width:"80%",

 },
 image:{
  position: "absolute",

  left: "50%",
  transform: "translate(-50%, -50%)",
  width:"100%",
  height:"80px",

},
  }))

const Blog = (({history})=>{
    const classes = useStyles()
    const [blogPosts, setBlogPosts] = React.useState([]);


    function createBlogPost(
      title,
      date,
      readTime,
      author,
      image1Alt,
      preview,
      ) {
        return {
            title,
            date,
            readTime,
            author,
            image1Alt,
            preview,
        };
      }



    const handleGetPosts = (() => {
        let items = [];

        firebase
          .firestore()
          .collection("Posts")
          .get()
          .then(
            (p) => {
              p.forEach((doc) => {
                let blogInformation = doc.data();
          console.log(blogInformation)
                items.push(
                  createBlogPost(
                    blogInformation.title,
                    blogInformation.date,
                    blogInformation.readTime,
                    blogInformation.author,
                    blogInformation.image1Alt,
                    blogInformation.preview,


                  )
                );

              });
              console.log(items)
              setBlogPosts(items);
              console.log(blogPosts)



            })})


            const [posts, setPosts] = useState([]);


            useEffect(() => {
              const fetchPosts = async () => {
                const res = await axios.get("/posts");
                setPosts(res.data);
                console.log()
              };
              fetchPosts();

            }, []);

    return(
        <div>



        <div className={classes.blogs}>
              <Grid
              container
              spacing={1}
              direction="row"
              justify="center"
              alignItems="flex-start"

          >

          {blogPosts.map((blogPosts) => (
            <Grid item xs={12} sm={4} md={3} >
            <Card 
            className={classes.card}
            onClick={() => {

              history.push(`post/${blogPosts.title}`)

          }}
            sx={{ maxWidth: 345 }}>
            <CardActionArea>
            <CardHeader
            key={posts.title}
            className={classes.cardHeader}
            title={posts.title}
            subheader={`${blogPosts.date} - ${blogPosts.readTime}`}
          />
<img alt={"hey"} url={Profile}></img>

            </CardActionArea>
          </Card>
          </Grid>

        ))}

          </Grid>

          </div>
                </div>
    )})

    export default Blog
