import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, useParams } from "react-router-dom";
import Box from '@mui/material/Box';
import InputLabel from '@mui/material/InputLabel';
import axios from "axios"
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';

const EditPost = (()=>{

  const [title, setTitle] = useState("");
  const [photo, setPhoto] = useState("");
        const [newPhoto, setNewPhoto] = useState(false)
  const [desc, setDesc] = useState("");
     const [file, setFile] = useState(null);

  const addPhoto = async (e) => {
        setFile(e)
    setNewPhoto(true)
        };



  let { id } =
  useParams();


      const PF = "http://localhost:5000/images/";
  useEffect(() => {
    const getPost = async () => {
      const res = await axios.get("/posts/" + id);
      setTitle(res.data.title);
      setDesc(res.data.desc);
      setPhoto(res.data.photo)
      setCategory()
    };
    getPost();
  }, [id]);





 const handleSubmit = async (e) => {
    let photo = ""
    e.preventDefault();
    const newPost = {
      title,
      desc,
      category,
      photo,
    };
    if (file) {
      const data =new FormData();
      console.log(file.name)
      const filename = Date.now() + file.name;
      data.append("name", filename);
      data.append("file", file);
      newPost.photo = filename;
      try {
        await axios.post("/upload", data);
      } catch (err) {}
    }
    try {

      const res = await axios.post("/posts", newPost);
      window.location.replace("/post/" + res.data._id);
    } catch (err) {}
  };

         const [category, setCategory] = React.useState('');

        const handleCategory = (event) => {
          setCategory(event.target.value);
        };


    return(
      <div className="write" >

        {newPhoto ? (<div>    {file && (
          <img className="writeImg" src={URL.createObjectURL(file)} alt="" />
        )}</div>):(<div> 
        {photo && <img className="writeImg" src={PF + photo} alt="" />}
        </div>)}



        <form className="writeForm" onSubmit={handleSubmit}>
          <div className="writeFormGroup">
            <label htmlFor="fileInput">
              <i className="writeIcon fas fa-plus"></i>
            </label>
            <input
              type="file"
              id="fileInput"
              style={{ display: "none" }}
              onChange={(e) => addPhoto(e.target.files[0])}
            />

            <input
              type="text"
              placeholder="Title"
              className="writeInput"
              autoFocus={true}
              value={title}
              onChange={e=>setTitle(e.target.value)}
            />

                         <Box            className="selectCategory" sx={{ minWidth: 120 }}> <FormControl fullWidth> <InputLabel value={category}>Category</InputLabel>
        <Select

          id="demo-simple-select"
          value={category}
          label="Category"
          onChange={handleCategory}
        >
          <MenuItem value={"My Experience"}>My Experience</MenuItem>
          <MenuItem value={"Cool Ideas"}>Cool Ideas</MenuItem>
          <MenuItem value={"Thoughts?"}>Thoughts?</MenuItem>
        </Select>
      </FormControl>
    </Box>

          </div>

          <div className="writeFormGroup">
            <textarea
              placeholder="Tell your story..."
              type="text"
              value={desc}
              className="writeInput writeText"
              onChange={e=>setDesc(e.target.value)}
            ></textarea>
          </div>
          <div className='ButtonGroup'>     <button className='writeButton' type="submit">
            Publish
          </button>
              <button       onClick={() => {

         axios.delete(`/posts/${id}`);
            window.location.replace("/authorHome/");
        }}  className='writeButton' >
            Delete
          </button></div>


        </form>



      </div>
    )})

    export default EditPost
