import React, { useRef } from "react";
import * as firebase from 'firebase'
import { useEffect } from "react";
import { useCallback } from "react";
import MuiAlert from "@mui/material/Alert";
import { BrowserRouter as Router, useParams } from "react-router-dom";
import Snackbar from "@mui/material/Snackbar";
import { set } from "@firebase/database";

import { getStorage } from "firebase/storage";
const Post = (()=>{

    const UrlInfo = (props) => {
        const [url, setUrl] = React.useState('')
    var gsReference = storage.refFromURL(`gs://wardrobe-asset-management.appspot.com/images/${props.name}`)
    gsReference.getDownloadURL().then(function(url) {
        setUrl(url)
        console.log(url);
    })
    return(




        <div>
        <img src= {url} alt="Hey" width="100px" height="100px"/>
        </div>)}

    let { id } =
    useParams();

    const [title, setTitle] = React.useState("");
    const [date, setDate] = React.useState("");
    const [readTime, setReadTime] = React.useState("");
    const [author, setAuthor] = React.useState("");
    const [paragraphs, setParagraphs] = React.useState("");
    const [image1, setImage1] = React.useState("");
    const [image2, setImage2] = React.useState("");
    const [image1Alt, setImage1Alt] = React.useState("");
    const [image2Alt, setImage2Alt] = React.useState("");




    const [openError, setOpenError] = React.useState(false);

    const [error, setError] = React.useState("");
      const handleError = () => {
        setOpenError(!openError);
      };


    const Alert = React.forwardRef(function Alert(props, ref) {
        return <MuiAlert elevation={6} ref={ref} variant="filled" {...props} />;
      });




    const handleGetPost = useCallback(() => {

        firebase
          .firestore()
          .collection("Blogs")
          .doc(id)
          .get()
          .then(
            (p) => {

                 setTitle(p.title)
                 setDate(p.date)
                 setReadTime(p.readTime)
                 setAuthor(p.author)
                 setParagraphs(p.paragraph)
setImage1(p.image1)
setImage2(p.image2)
setImage2Alt(p.image2Alt)
setImage1Alt(p.setImage1Alt)




              })
              .catch((error) => {
            console.error("Error retrieving document: ", error);
            setError(error.message);
            handleError();
          });
      }, []);

    return(
        <div>
        <Snackbar
        open={openError}
        autoHideDuration={6000}
        onClose={handleError}
      >
        <Alert
          onClose={handleError}
          severity="success"
          sx={{ width: "100%" }}
        >
         {error}
        </Alert>
      </Snackbar>

        <h2>{title}</h2>
        <p>{author}</p>
        <p>{date}</p>
        <p>{readTime}</p>
        <p>{paragraphs}</p>
        <img alt={image1Alt} url={image1}></img>
        <img alt={image2Alt} url={image2}></img>

        </div>
    )})

    export default Post
