import React, { useState } from 'react'
import Button from '@material-ui/core/Button'
import { storage } from '../index'
import firebase from 'firebase'
import { makeStyles } from '@material-ui/core/styles'
import Checkbox from '@material-ui/core/Checkbox'
import Select from '@material-ui/core/Select'
import InputLabel from '@material-ui/core/InputLabel'
import MenuItem from '@material-ui/core/MenuItem'
import Container from '@material-ui/core/Container'
import { createMuiTheme } from '@material-ui/core/styles'

const theme = createMuiTheme({
    palette: {
        primary: {
            main: '#4e18ac',
        },
    },
})
const useStyles = makeStyles((theme) => ({
    size: {
        maxWidth: '300px',
        maxHeight: '300px',
    },
    formControl: {
        margin: theme.spacing(1),
        minWidth: 120,
    },

    divWrap: {
        display: 'flex',
        flexDirection: 'column',
        // minHeight: '700px',
        alignItems: 'center',
        padding: '10px',
        justifyContent: 'space-between',
    },

    div2Wrap: {
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        padding: '10px',
        width: '70%',
        maxWidth: '1000px',
    },

    div3Wrap: {
        display: 'flex',

        flexDirection: 'row',
        alignItems: 'top',
        padding: '4px',
        justifyContent: 'space-around',
        width: '100%',
        margin: '10px 0',
    },
    buttonWrap: {
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
    },
    inputWrap: {
        display: 'flex',
        flexDirection: 'column',
        width: '100%',
        alignItems: 'flex-start',
    },

    h1Style: {
        color: '#4e18ac',
    },
    buttonStyle: {
        backgroundColor: '#4e18ac',
        color: 'white',
        width: '200px',
        height: '50px',
        textTransform: 'none',
    },
}))
const UploadImage = () => {
    const classes = useStyles()
    const allInputs = { imgUrl: '' }
    const [imageAsFile, setImageAsFile] = useState('')
    const [imageAsUrl, setImageAsUrl] = useState(allInputs)

    //sign in
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')

    //product info
    const [type, setType] = useState('')
    const [size, setSize] = React.useState('')
    const [title, setTitle] = React.useState('')
    const [brand, setBrand] = useState('')
    const [colour, setColour] = useState('')
    const [description, setDescription] = useState('')

    //selling info
    const [location, setLocation] = useState('')
    const [price, setPrice] = useState('')

    //options
    const [checked, setChecked] = React.useState(false)

    //handles
    const handleSizeChange = (event) => {
        setSize(event.target.value)
    }

    const handleTypeChange = (event) => {
        setType(event.target.value)
    }

    const handleCheckChange = (event) => {
        setChecked(event.target.checked)
    }

    console.log(imageAsFile)
    const handleImageAsFile = (e) => {
        const image = e.target.files[0]
        setImageAsFile((imageFile) => image)
    }

    const handleFireBaseUpload = (e) => {
        e.preventDefault()
        console.log('start of upload')
        // async magic goes here...
        if (imageAsFile === '') {
            console.error(
                `not an image, the image file is a ${typeof imageAsFile}`
            )
        }

        const uploadTask = storage.ref(`/images/ff`).put(imageAsFile)
        //initiates the firebase side uploading
        uploadTask.on(
            'state_changed',
            (snapShot) => {
                //takes a snap shot of the process as it is happening
                console.log(snapShot)
            },
            (err) => {
                //catches the errors
                console.log(err)
            },
            () => {
                // gets the functions from storage refences the image storage in firebase by the children
                // gets the download url then sets the image from firebase as the value for the imgUrl key:
                storage
                    .ref('images')
                    .child(imageAsFile)
                    .getDownloadURL()
                    .then((fireBaseUrl) => {
                        setImageAsUrl((prevObject) => ({
                            ...prevObject,
                            imgUrl: fireBaseUrl,
                        }))
                    })
            }
        )
    }




    return (
        <div className="App">
            <div className={classes.divWrap}>
                <h1 className={classes.h1Style}>Product Upload</h1>
                <div className={classes.div2Wrap}>
                    <br />
                    <input
                        style={{
                            marginTop: '10px',
                            marginBottom: '10px',
                            width: '100%',
                        }}
                        value={title}
                        placeholder="Item title"
                        onChange={(e) => setTitle(e.currentTarget.value)}
                    />
                    <br />
                    <div className={classes.div3Wrap}>
                        <div className={classes.inputWrap}>
                            <InputLabel id="product_size">Size</InputLabel>
                            <Select
                                className={classes.formControl}
                                value={size}
                                onChange={handleSizeChange}
                                style={{
                                    margin: 0,
                                }}
                            >
                                <MenuItem value={6}>6</MenuItem>
                                <MenuItem value={8}>8</MenuItem>
                                <MenuItem value={10}>10</MenuItem>
                                <MenuItem value={12}>12</MenuItem>
                                <MenuItem value={14}>14</MenuItem>
                                <MenuItem value={16}>16</MenuItem>
                            </Select>
                        </div>
                        <div
                            className={classes.inputWrap}
                            style={{
                                margin: 0,
                            }}
                        >
                            <InputLabel id="product_type">Type</InputLabel>
                            <Select
                                className={classes.formControl}
                                value={type}
                                onChange={handleTypeChange}
                                style={{
                                    margin: '0px',
                                }}
                            >
                                <MenuItem value={'Skirt'}>Skirt</MenuItem>
                                <MenuItem value={'Top'}>Top</MenuItem>
                                <MenuItem value={'Dress'}>Dress</MenuItem>
                                <MenuItem value={'Pants'}>Pants</MenuItem>
                                <MenuItem value={'Accessories'}>
                                    Accessories
                                </MenuItem>
                                <MenuItem value={'Shoes'}>Shoes</MenuItem>
                            </Select>
                        </div>
                    </div>

                    <br />
                    <input
                        value={colour}
                        placeholder="Colour"
                        style={{
                            marginTop: '10px',
                            marginBottom: '10px',
                            width: '100%',
                        }}
                        onChange={(e) => setColour(e.currentTarget.value)}
                    />
                    <br />
                    <input
                        value={brand}
                        placeholder="Brand"
                        style={{
                            marginTop: '10px',
                            marginBottom: '10px',
                            width: '100%',
                        }}
                        onChange={(e) => setBrand(e.currentTarget.value)}
                    />
                    <br />
                    <input
                        value={description}
                        placeholder="Description"
                        style={{
                            marginTop: '10px',
                            marginBottom: '10px',
                            width: '100%',
                        }}
                        onChange={(e) => setDescription(e.currentTarget.value)}
                    />
                    <br />
                    <div style={{ margin: '10px 0 0' }}>
                        <input type="file" onChange={handleImageAsFile} />
                        <div
                            className={classes.size}
                            style={{ margin: '0 0 10px' }}
                        >
                            <img
                                src={imageAsUrl.imgUrl}
                                style={{ maxWidth: '300px' }}
                                alt=""
                            />
                        </div>
                    </div>
                    <p4 style={{ fontSize: '15px', margin: '0 0 10px' }}>
                        Would you like to sell this?
                    </p4>
                    <Checkbox
                        checked={checked}
                        onChange={handleCheckChange}
                        color="primary"
                        inputProps={{ 'aria-label': 'primary checkbox' }}
                    />
                    {checked ? (
                        <div style={{ width: '100%' }}>
                            <input
                                value={location}
                                placeholder="Location"
                                style={{
                                    marginTop: '10px',
                                    marginBottom: '10px',
                                    width: '100%',
                                }}
                                onChange={(e) =>
                                    setLocation(e.currentTarget.value)
                                }
                            />
                            <input
                                value={price}
                                placeholder="Price"
                                style={{
                                    marginTop: '10px',
                                    marginBottom: '10px',
                                    width: '100%',
                                }}
                                onChange={(e) =>
                                    setPrice(e.currentTarget.value)
                                }
                            />
                        </div>
                    ) : (
                        ''
                    )}
                    <form
                        onSubmit={handleFireBaseUpload}
                        style={{ margin: '10px 0' }}
                    >
                        <button
                            className={classes.buttonStyle}
                            onClick={() => {


                            }}
                        >
                            upload to firebase
                        </button>
                    </form>
                </div>
            </div>
        </div>
    )
}

export default UploadImage
