import React, { useRef } from "react";
import firebase from "firebase";
import { useEffect } from "react";
import { useCallback } from "react";
import Card from '@mui/material/Card';
import CardHeader from '@mui/material/CardHeader';
import CardMedia from '@mui/material/CardMedia';
import CardContent from '@mui/material/CardContent';
import Typography from '@mui/material/Typography';
import { CardActionArea } from '@mui/material';
import MuiAlert from "@mui/material/Alert";
import { storage } from '../index'
import { makeStyles } from '@material-ui/core/styles'
import Snackbar from "@mui/material/Snackbar";
import { useHistory } from 'react-router-dom'
import {
  Grid
} from '@material-ui/core/'
const BlogPosts = (({history})=>{


  const useStyles = makeStyles((theme) => ({

    card:{
       borderStyle:"solid",
       borderColor:"#b291f2",
       width:"100%",
       height:"100%",

    },
    cardHeader:{
    background: "#e2d7f7",
    color:"white"

   },
   blogs:{
justifyContent:"center",
alignContent:"center",
    width:"80%",
    height:"100%",

 },
  }))

  const classes = useStyles()
    const UrlInfo = (props) => {
      console.log(props.name)
        const [url, setUrl] = React.useState('')
    var gsReference = storage.refFromURL(`gs://techinwithella.appspot.com/images/${props.name}`)
    gsReference.getDownloadURL().then(function(url) {
        setUrl(url)
        console.log(url);
    })

    let history = useHistory()
    return(




        <div>
        <img src= {url} alt={props.name} width="100%" height="100%"/>
        </div>)}

    const [blogPosts, setBlogPosts] = React.useState([]);


    function createBlogPost(
      title,
      date,
      readTime,
      author,
      image1Alt,
      preview,
      ) {
        return {
            title,
            date,
            readTime,
            author,
            image1Alt,
            preview,
        };
      }




      const [openError, setOpenError] = React.useState(false);
      const [error, setError] = React.useState("");
      const handleError = () => {
        setOpenError(!openError);
      };


      const Alert = React.forwardRef(function Alert(props, ref) {
        return <MuiAlert elevation={6} ref={ref} variant="filled" {...props} />;
      });

    const handleGetPosts = useCallback(() => {
        let items = [];

        firebase
          .firestore()
          .collection("Posts")
          .get()
          .then(
            (p) => {
              p.forEach((doc) => {
                let blogInformation = doc.data();
          console.log(blogInformation)
                items.push(
                  createBlogPost(
                    blogInformation.title,
                    blogInformation.date,
                    blogInformation.readTime,
                    blogInformation.author,
                    blogInformation.image1Alt,
                    blogInformation.preview,


                  )
                );

              });
              console.log(items)
              setBlogPosts(items);
              console.log(blogPosts)



            },
            [setBlogPosts]
          )
          .catch((error) => {
            console.error("Error retrieving document: ", error);
            setError(error.message);
            handleError()
          });
      }, []);


      useEffect(() => {

        handleGetPosts()
        console.log(blogPosts)
              }, []);




    return(
        <div>

        <Snackbar
        open={openError}
        autoHideDuration={6000}
        onClose={handleError}
      >
        <Alert
          onClose={handleError}
          severity="success"
          sx={{ width: "100%" }}
        >
         {error}
        </Alert>
      </Snackbar>
<div className={classes.blogs}>
      <Grid
      container
      spacing={1}
      direction="row"
      justify="center"
      alignItems="center"

  >

  {blogPosts.map((blogPosts) => (
    <Grid item xs={12} sm={4} md={3} >
    <Card 
    className={classes.card}
    onClick={() => {
   history.push(`posts/${blogPosts.title}`)


  }}
    sx={{ maxWidth: 345 }}>
    <CardActionArea>
    <CardHeader
    key={blogPosts.title}
    className={classes.cardHeader}
    title={blogPosts.title}
    subheader={`${blogPosts.date} - ${blogPosts.readTime}`}
  />
  <UrlInfo name={blogPosts.image1Alt}></UrlInfo>

    </CardActionArea>
  </Card>
  </Grid>

))}

  </Grid>

  </div>
        </div>
    )})

    export default BlogPosts
