import React, { useState } from 'react'
import Button from '@material-ui/core/Button'
import { storage } from '../index'
import firebase from 'firebase'
import { makeStyles } from '@material-ui/core/styles'
import Checkbox from '@material-ui/core/Checkbox'
import Select from '@material-ui/core/Select'
import InputLabel from '@material-ui/core/InputLabel'
import MenuItem from '@material-ui/core/MenuItem'
import Container from '@material-ui/core/Container'
import { createMuiTheme } from '@material-ui/core/styles'
import { AutoFixHighTwoTone } from '@mui/icons-material'
import { TextField } from '@material-ui/core'

const theme = createMuiTheme({
    palette: {
        primary: {
            main: '#4e18ac',
        },
    },
})
const useStyles = makeStyles((theme) => ({
    size: {
        maxWidth: '300px',
        maxHeight: '300px',
    },
    formControl: {
        margin: theme.spacing(1),
        minWidth: 120,
    },

    divWrap: {
        display: 'flex',
        flexDirection: 'column',
        // minHeight: '700px',
        alignItems: 'center',
        padding: '10px',
        justifyContent: 'space-between',
    },

    div2Wrap: {
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        padding: '10px',
        width: '70%',
        maxWidth: '1000px',
    },

    div3Wrap: {
        display: 'flex',

        flexDirection: 'row',
        alignItems: 'top',
        padding: '4px',
        justifyContent: 'space-around',
        width: '100%',
        margin: '10px 0',
    },
    buttonWrap: {
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
    },
    inputWrap: {
        display: 'flex',
        flexDirection: 'column',
        width: '100%',
        alignItems: 'flex-start',
    },

    h1Style: {
        color: '#4e18ac',
    },
    buttonStyle: {
        backgroundColor: '#4e18ac',
        color: 'white',
        width: '200px',
        height: '50px',
        textTransform: 'none',
    },
}))
const UploadImage = () => {
    const classes = useStyles()
    const allInputs = { imgUrl: '' }
    const [imageAsFile, setImageAsFile] = useState('')
    const [imageAsUrl, setImageAsUrl] = useState(allInputs)
    const [title, setTitle] = React.useState("");
    const [date, setDate] = React.useState("");
    const [readTime, setReadTime] = React.useState("");
    const [author, setAuthor] = React.useState("");
    const [paragraphs, setParagraphs] = React.useState("");
    const [image1, setImage1] = React.useState("");
    const [image2, setImage2] = React.useState("");
    const [image1Alt, setImage1Alt] = React.useState("");
    const [image2Alt, setImage2Alt] = React.useState("");


    const uploadData = () => {
        firebase.firestore().collection("Posts").doc(title).set({
            title: title,
            author: author,
            date: date,
            readTime: readTime,
            image1Alt: image1Alt,
        })}

    console.log(imageAsFile)
    const handleImageAsFile = (e) => {
        const image = e.target.files[0]
        setImageAsFile((imageFile) => image)
    }

    const handleFireBaseUpload = (e) => {
        e.preventDefault()
        console.log('start of upload')
        // async magic goes here...
        if (imageAsFile === '') {
            console.error(
                `not an image, the image file is a ${typeof imageAsFile}`
            )
        }


        const uploadTask = storage.ref(`/images/${title}`).put(imageAsFile)
        //initiates the firebase side uploading
        uploadTask.on(
            'state_changed',
            (snapShot) => {
                //takes a snap shot of the process as it is happening
                console.log(snapShot)
            },
            (err) => {
                //catches the errors
                console.log(err)
            },
            () => {
                // gets the functions from storage refences the image storage in firebase by the children
                // gets the download url then sets the image from firebase as the value for the imgUrl key:
                storage
                    .ref('images')
                    .getDownloadURL()
                    .then((fireBaseUrl) => {
                        setImageAsUrl((prevObject) => ({
                            ...prevObject,
                            imgUrl: fireBaseUrl,
                        }))
                    })
            }
        )
    }


    return (

            <div className={classes.divWrap}>
            <h1 className={classes.h1Style}>Upload Post</h1>
            <div className={classes.div2Wrap}>
                <br />
                <TextField
                    style={{
                        marginTop: '10px',
                        marginBottom: '10px',
                        width: '100%',
                    }}
                    value={title}
                    placeholder="Title"
                    onChange={(e) => setTitle(e.currentTarget.value)}
                />
                <br />



                <TextField
                    value={author}
                    placeholder="Author"
                    style={{
                        marginTop: '10px',
                        marginBottom: '10px',
                        width: '100%',
                    }}
                    onChange={(e) => setAuthor(e.currentTarget.value)}
                />
                <br />
                <TextField
                    value={date}
                    placeholder="Date"
                    style={{
                        marginTop: '10px',
                        marginBottom: '10px',
                        width: '100%',
                    }}
                    onChange={(e) => setDate(e.currentTarget.value)}
                />
                <br />
                <TextField
                    value={readTime}
                    placeholder="Read Time"
                    style={{
                        marginTop: '10px',
                        marginBottom: '10px',
                        width: '100%',
                    }}
                    onChange={(e) => setReadTime(e.currentTarget.value)}
                />
                <br />

                <div style={{ margin: '10px 0 0' }}>
                <TextField
                value={image1Alt}
                placeholder="Image 1 ALT"
                style={{
                    marginTop: '10px',
                    marginBottom: '10px',
                    width: '100%',
                }}
                onChange={(e) => setImage1Alt(e.currentTarget.value)}
            />

                    <br />
                    <div style={{ margin: '10px 0 0' }}>
                        <input type="file" onChange={handleImageAsFile} />
                        <div
                            className={classes.size}
                            style={{ margin: '0 0 10px' }}
                        >
                            <img
                                src={imageAsUrl.imgUrl}
                                style={{ maxWidth: '300px' }}
                                alt=""
                            />
                        </div>
                    </div>

                    <form
                        onSubmit={handleFireBaseUpload}
                        style={{ margin: '10px 0' }}
                    >
                        <button
                            className={classes.buttonStyle}
                            onClick={() => {
                                uploadData()
                            }}
                        >
                            Submit
                        </button>
                    </form>
                </div>
            </div>
        </div>
    )
}

export default UploadImage
