            height:"100%",

         },
         cardHeader:{
         background: "#e2d7f7",
         color:"white",
         fontSize:"1vw",

        },

        image:{

            width: "100%",
            height: "250px",


        objectFit: "cover"


        },

        category:{

          borderTop:"solid",
          borderBottom:"solid",
          borderTopWidth:"1px",
          borderBottomWidth:"1px",
          padding:"5px",




        },
            }))
    const PF = "http://localhost:5000/images/";
    const [posts, setPosts] = useState([]);



    useEffect(() => {

        const fetchPosts = async () => {
         const res = await axios.get("/posts");
         console.log(res)
         setPosts(res.data)
         setFilter(res.data)
        };

        fetchPosts();

      }, []);



      const [filter, setFilter] = useState([]);

  const handleSubmit = async (props) => {
