import React, { useState, useEffect } from 'react';
import ReactMarkdown from "react-markdown";
import MuiAlert from "@mui/material/Alert";
import { BrowserRouter as Router, useParams } from "react-router-dom";
import Snackbar from "@mui/material/Snackbar";
import { set } from "@firebase/database";
import axios from "axios"
import AddComments from '../Components/addComment'
import DisplayComments from '../Components/displayComments'
import { makeStyles } from '@material-ui/core/styles'

const Post = (()=>{


  let { id } =
  useParams();


  const [post, setPost] = React.useState([]);
  const [title, setTitle] = useState("");
  const [desc, setDesc] = useState("");

  useEffect(() => {
    const getPost = async () => {
      const res = await axios.get("/posts/" + id);
      setPost(res.data);
      setTitle(res.data.title);
      setDesc(res.data.desc);
      setPostId(res.data._id)
    };
    getPost();
  }, [id]);










    const [openError, setOpenError] = React.useState(false);

    const [error, setError] = React.useState("");
      const handleError = () => {
        setOpenError(!openError);
      };

    const Alert = React.forwardRef(function Alert(props, ref) {
        return <MuiAlert elevation={6} ref={ref} variant="filled" {...props} />;
      });

      const PF = "http://localhost:5000/images/";


      const useStyles = makeStyles((theme) => ({

        writeImg:{

          width: "90%",
          height: "250px",         
           borderRadius: "10px",
          objectFit: "cover",
        },
        body:{

          width: "70vw",
          margin:'1px',


        },
        markdown:{

          width: "70vw",
          margin:'1px',


        }


      }))

      const [comments, setComments] = useState([]);




      useEffect(() => {
        const fetchPosts = async () => {

         const res = await axios.get(`/comment/${post._id}`);
         console.log(res)
         setComments(res.data)

        };
        fetchPosts();

      }, []);


      const [name, setName] = useState("");
      const [email, setEmail] = useState("");
      const [content, setContent] = useState(null);
    let postId = props.id
    console.log(props.id)




      const handleSubmit = async (e) => {
        console.log("hey")
        e.preventDefault();
        const newComment = {
          name,
          email,
          postId,
          content,
        };

        try {
          console.log("post",postId)
          const res = await axios.post("/comment", newComment);
          console.log("bingo")
          window.location.replace("/post/" + res.data.postId);
        } catch (err) {}
      };


      const classes = useStyles()
    return(
        <div>
        <Snackbar
        open={openError}
        autoHideDuration={6000}
        onClose={handleError}
      >
        <Alert
          onClose={handleError}
          severity="success"
          sx={{ width: "100%" }}
        >

         {error}
        </Alert>
      </Snackbar>

      {post.photo && <img className={classes.writeImg} src={PF + post.photo} alt="" />}
      <div className={classes.body}>
        <h2 >{title}</h2>

        <ReactMarkdown className={classes.markdown}  >{desc}</ReactMarkdown>

        </div>
        <div className="write">



        <form className="writeForm" onSubmit={handleSubmit}>
          <div className="writeFormGroup">


            <input
              type="text"
              placeholder="Name"
              className="writeInput"
              autoFocus={true}
              onChange={e=>setName(e.target.value)}
            />



          </div>

          <div className="writeFormGroup">


          <input
            type="text"
            placeholder="Email"
            className="writeInput"
            autoFocus={true}
            onChange={e=>setEmail(e.target.value)}
          />



        </div>

          <div className="writeFormGroup">
            <textarea
              placeholder="Join the discussion"
              type="text"
              className="writeInput writeText"
              onChange={e=>setContent(e.target.value)}
            ></textarea>
          </div>
          <button className="writeSubmit" type="submit">
            Publish
          </button>
        </form>
      </div>
        <div >


        <div className={classes.body} >
        <h3>Comments</h3>
          {comments.map((comments) => (
            <div className={classes.container}>
            <p>{comments.name}</p>
            <ReactMarkdown>{comments.content}</ReactMarkdown>

            </div>
        ))}

        </div>

          </div>

        </div>
    )})

    export default Post
