      <textarea value={content}
      onChange={(e) => setContent(e.target.value)} className={classes.textArea}  placeholder= "Join the discussion!"/>

      </div>
      <button   onClick={() => { 



        const newComment = {
        name,
        email,
        postId,
        content,
        replyTo,
      };

      try {
        console.log(name)
        console.log(email)
        console.log(content)
