import React, { useState, useEffect } from 'react';
import ReactMarkdown from "react-markdown";
import MuiAlert from "@mui/material/Alert";
import { BrowserRouter as Router, useParams } from "react-router-dom";
import Snackbar from "@mui/material/Snackbar";
import { set } from "@firebase/database";
import FavoriteIcon from '@mui/icons-material/Favorite';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import axios from "axios"
import AddIcon from '@mui/icons-material/Add';
import IconButton from '@mui/material/IconButton'
import { makeStyles } from '@material-ui/core/styles'
import { Button } from "@material-ui/core";
const Post = (()=>{


  let { id } =
  useParams();


  const [post, setPost] = React.useState([]);
  const [title, setTitle] = useState("");
  const [desc, setDesc] = useState("");
  const [postId, setPostId] = React.useState("");
  useEffect(() => {
    const getPost = async () => {
      const posts = await axios.get("/posts/" + id);
      setPost(posts.data);
      setTitle(posts.data.title);
      setDesc(posts.data.desc);
      setPostId(posts.data._id)


      const comments = await axios.get(`/comment/${posts.data._id}`);
      console.log(comments)
      setComments(comments.data)

     };

    getPost();
  }, [id]);










    const [openError, setOpenError] = React.useState(false);

    const [error, setError] = React.useState("");
      const handleError = () => {
        setOpenError(!openError);
      };

    const Alert = React.forwardRef(function Alert(props, ref) {
        return <MuiAlert elevation={6} ref={ref} variant="filled" {...props} />;
      });

      const PF = "http://localhost:5000/images/";


      const useStyles = makeStyles((theme) => ({

        writeImg:{

          width: "100%",
          height: "250px",         
           borderRadius: "10px",
          objectFit: "cover",
          marginTop:"2%",
        },
        body:{

          width:"80%",
          marginLeft:"5%",


        },
        markdown:{

          width: "60vw",
          margin:'1px',


        },
        commentHeader:{
         display:'flex',
         flexDirection:"row", 
     backgroundColor:"#d6d6d4",
     width:"100%",


        },
        commentBody:{


          margin:'1px',
          borderStyle:"solid",
          borderColor:"#d6d6d4",
          borderRadius:"15px",
          width:"100%",


        },

        addBody:{


          margin:'1px',


          width:"100%",



        },

        addCommentBody:{

          flexDirection:"row", 
          margin:'1px',




        },
    addCommentButton:{
backgroundColor:"#2195ed"
    },

        textArea:{


          margin:'1px',


          width:"100%",

        },
        commentSection:{

          display:'flex',
          flexDirection:"column", 
          width:"100%",

        },
        commentText:{

          margin:"1%",

        }
        ,commentName:{
          color:"#2195ed",
          marginLeft: "1%",
          marginRight:"2%",
        }
        ,  commentWrite:{

         width:"50%",

        }




      }))

      const [comments, setComments] = useState([]);



      const [heart, setHeart] = useState(false);
      const [name, setName] = useState("");
      const [email, setEmail] = useState("");
      const [content, setContent] = useState(null);



      function Like() {

        if (heart) {
          return <FavoriteIcon />;
        }
        return <FavoriteBorderIcon  />;
      }


      const handleSubmit = async (e) => {


        const newComment = {
          name,
          email,
          postId,
          content,
        };

        try {
          console.log(name)
          console.log(email)
          console.log(content)
          await axios.post("/comment", newComment);
          console.log("bingo")
          window.location.replace("/post/" + postId);
        } catch (err) {}
      };


      const classes = useStyles()
    return(
        <div className={classes.body}>
        <Snackbar
        open={openError}
        autoHideDuration={6000}
        onClose={handleError}
      >
        <Alert
          onClose={handleError}
          severity="success"
          sx={{ width: "100%" }}
        >

         {error}
        </Alert>
      </Snackbar>
      <div className={classes.body}>
      {post.photo && <img className={classes.writeImg} src={PF + post.photo} alt="" />}

        <h2 >{title}</h2>

        <ReactMarkdown className={classes.markdown}  >{desc}</ReactMarkdown>

        </div>

        <div className={classes.commentSection}>

        <div >


        <div className={classes.body} >
        <h3>Comments</h3>
          {comments.map((comments) => (
            <div>
                 <div className={classes.commentBody}>
            <div className={classes.commentHeader}>
            <p  className={classes.commentName}>{comments.name}</p>
            <br/>
            <p>{new Date(comments.createdAt).toDateString()}</p>

      <IconButton onClick={() => {
   setHeart(!heart)

    }}
            key={`addButton`}

            className={classes.headerButton}
          >
          <Like />

          </IconButton>
          <Button className={classes.commentName} >Reply</Button>
            </div>

            <ReactMarkdown className={classes.commentText} >{comments.content}</ReactMarkdown>
          </div>
          <div>
          {comments.map((comments) => (
            <div>
                 <p>hey</p>
            </div>
        ))}
          </div>

            </div>
        ))}

        </div>

          </div>
          <div className={classes.body} >


          <div  className={classes.addCommentBody} >
 <p>Add Comment</p>
        <input className={classes.inputInfo}    onChange={(e) => setName(e.target.value)} placeholder="Name"></input>
            <input className={classes.inputInfo}  onChange={(e) => setEmail(e.target.value)}  placeholder="Email"></input>

            <textArea  className={classes.textArea}  onChange={(e) => setContent(e.target.value)}  placeholder= "Join the discussion!"></textArea>

            </div>
            <button   onClick={() => handleSubmit()} className={classes.commentButton}>Add Comment</button>
        </div>
        </div>

        </div>
    )})

    export default Post
