    const [error, setError] = React.useState("");
      const handleError = () => {
        setOpenError(!openError);
      };

    const Alert = React.forwardRef(function Alert(props, ref) {
        return <MuiAlert elevation={6} ref={ref} variant="filled" {...props} />;
      });

      const PF = "http://localhost:5000/images/";


      const useStyles = makeStyles((theme) => ({

        writeImg:{

          width: "100%",
          height: "250px",         
           borderRadius: "10px",
          objectFit: "cover",
          marginTop:"2%",
        },
        body:{

          width:"80%",
          marginLeft:"5%",


        },
        markdown:{

          width: "80vw",
          margin:'1px',


        },
        commentHeader:{
         display:'flex',
         flexDirection:"row", 
     backgroundColor:"#d6d6d4",
     width:"100%",


        },
        commentBody:{


          margin:'1px',
          borderStyle:"solid",
          borderColor:"#d6d6d4",
          borderRadius:"15px",
          width:"100%",


        },

        addCommentBody:{


          margin:'1px',
          borderStyle:"solid",
          borderColor:"#d6d6d4",

          width:"30vw",



        },
        inputInfo:{


 margin:"1px",
          borderStyle:"solid",
          borderColor:"#d6d6d4",

   width:"50%"

        },

        textArea:{


          margin:'1px',
          borderStyle:"solid",
          borderColor:"#d6d6d4",

          width:"30vw",

        },
        commentSection:{

          display:'flex',
          flexDirection:"column", 
          width:"100%",

        },
        commentText:{

          margin:"1%",

        }
        ,  commentWrite:{

         width:"50%",

        }




      }))

      const [comments, setComments] = useState([]);





      const [name, setName] = useState("");
      const [email, setEmail] = useState("");
      const [content, setContent] = useState(null);





      const handleSubmit = async (e) => {

        e.preventDefault();
        const newComment = {
          name,
          email,
          postId,
          content,
        };

        try {
          console.log("hey")
          const post = await axios.post("/comment", newComment);
          console.log("bingo")
          window.location.replace("/post/" + postId);
        } catch (err) {}
      };


      const classes = useStyles()
    return(
        <div className={classes.body}>
        <Snackbar
        open={openError}
        autoHideDuration={6000}
        onClose={handleError}
      >
        <Alert
          onClose={handleError}
          severity="success"
          sx={{ width: "100%" }}
        >

         {error}
        </Alert>
      </Snackbar>
      <div className={classes.body}>
      {post.photo && <img className={classes.writeImg} src={PF + post.photo} alt="" />}

        <h2 >{title}</h2>

        <ReactMarkdown className={classes.markdown}  >{desc}</ReactMarkdown>

        </div>

        <div className={classes.commentSection}>

        <div >


        <div className={classes.body} >
        <h3>Comments</h3>
          {comments.map((comments) => (
            <div>
                 <div className={classes.commentBody}>
            <div className={classes.commentHeader}>
            <p>{comments.name}</p>
            <br/>
            <p>{new Date(comments.createdAt).toDateString()}</p>

            </div>

            <ReactMarkdown className={classes.commentText} >{comments.content}</ReactMarkdown>
          </div>

            </div>
        ))}

        </div>

          </div>
          <div className={classes.body} >


          <div  className={classes.addCommentBody} >
 <p>Add Comment</p>
        <input className={classes.inputInfo} placeholder="name"></input>
            <input className={classes.inputInfo} placeholder="email"></input>
            </div>
            <div>
            <textArea  className={classes.textArea} placeholder= "Join the discussion!"></textArea>

            </div>
            <button>Add Comment</button>
        </div>
        </div>

        </div>
    )})

    export default Post
