      });

      const PF = "http://localhost:5000/images/";


      const useStyles = makeStyles((theme) => ({

        writeImg:{
          marginLeft: "150px",
          width: "70vw",
          height: "250px",         
           borderRadius: "10px",
          objectFit: "cover",
        }

      }))


      const classes = useStyles()
