import React, { useState } from 'react'
import Button from '@material-ui/core/Button'
import { storage } from '../index'
import firebase from 'firebase'
import { makeStyles } from '@material-ui/core/styles'
import Checkbox from '@material-ui/core/Checkbox'
import Select from '@material-ui/core/Select'
import InputLabel from '@material-ui/core/InputLabel'
import MenuItem from '@material-ui/core/MenuItem'
import Container from '@material-ui/core/Container'

import TextField from '@mui/material/TextField';

const useStyles = makeStyles((theme) => ({

}))
const UploadImage = () => {
  const [title, setTitle] = React.useState("");
  const [date, setDate] = React.useState("");
  const [readTime, setReadTime] = React.useState("");
  const [author, setAuthor] = React.useState("");
  const [paragraphs, setParagraphs] = React.useState("");
  const [image1, setImage1] = React.useState("");
  const [image2, setImage2] = React.useState("");
  const [image1Alt, setImage1Alt] = React.useState("");
  const [image2Alt, setImage2Alt] = React.useState("");



    const classes = useStyles()
    const allInputs = { imgUrl: '' }

    const [imageAsUrl1, setImageAsUrl1] = useState(allInputs)
    const [imageAsUrl2, setImageAsUrl2] = useState(allInputs)


    const [imageAsFileImage2, setImageAsFileImage2] = useState('')

    const handleImageAsFileImage2 = (e) => {
        const image = e.target.files[0]
        setImageAsFileImage2((imageAsFileImage2) => image)
    }
    const [imageAsFileImage1, setImageAsFileImage1] = useState('')
    const handleImageAsFileImage1 = (e) => {
      const image = e.target.files[0]
      setImageAsFileImage1((imageAsFileImage1) => image)
  }



    const handleFireBaseImage2Upload= (e) => {

        console.log('start of upload')
        // async magic goes here...
        if (imageAsFileImage2 === '') {
            console.error(
                `not an image, the image file is a ${typeof imageAsFileImage2}`
            )
        }

        const uploadTask2 = storage.ref(`/images/${image1Alt}`).put(imageAsFileImage2)
        //initiates the firebase side uploading
        uploadTask2.on(
            'state_changed',
            (snapShot) => {
                //takes a snap shot of the process as it is happening
                console.log(snapShot)
            },
            (err) => {
                //catches the errors
                console.log(err)
            },
            () => {
                // gets the functions from storage refences the image storage in firebase by the children
                // gets the download url then sets the image from firebase as the value for the imgUrl key:
                storage
                    .ref('images')
                    .child(imageAsFileImage2)
                    .getDownloadURL()
                    .then((fireBaseUrl) => {
                        setImageAsUrl2((prevObject) => ({
                            ...prevObject,
                            imgUrl2: fireBaseUrl,
                        }))
                    })
            }
        )
    }


    const handleFireBaseImage1Upload = (e) => {

      console.log('start of upload')
      // async magic goes here...
      if (imageAsFileImage1 === '') {
          console.error(
              `not an image, the image file is a ${typeof imageAsFileImage1}`
          )
      }

      const uploadTask1 = storage.ref(`/images/${image1Alt}`).put(imageAsFileImage1)
      //initiates the firebase side uploading
      uploadTask1.on(
          'state_changed',
          (snapShot) => {
              //takes a snap shot of the process as it is happening
              console.log(snapShot)
          },
          (err) => {
              //catches the errors
              console.log(err)
          },
          () => {
              // gets the functions from storage refences the image storage in firebase by the children
              // gets the download url then sets the image from firebase as the value for the imgUrl key:
              storage
                  .ref('images')
                  .child(imageAsFileImage1)
                  .getDownloadURL()
                  .then((fireBaseUrl) => {
                      setImageAsUrl1((prevObject) => ({
                          ...prevObject,
                          imgUrl1: fireBaseUrl,
                      }))
                  })
          }
      )
  }




    return (

            <div className={classes.divWrap}>
            <TextField id="outlined-basic" label="Title" variant="outlined" />
            <TextField id="outlined-basic" label="Author" variant="outlined" />
            <TextField id="outlined-basic" label="Date" variant="outlined" />
            <TextField id="outlined-basic" label="Read Time" variant="outlined" />
            <TextField id="outlined-basic" label="Image1Alt" variant="outlined" />

            <input type="file" onChange={handleImageAsFileImage1} />

                <img
                    src={imageAsUrl1.imgUrl1}

                    alt="ImageAlt1"
                />

            <TextField id="outlined-basic" label="Image2Alt" variant="outlined" />


            <input type="file" onChange={handleImageAsFileImage2} />

                <img
                    src={imageAsUrl2.imgUrl2}

                    alt="Imag"
                />









                        <button
                            className={classes.buttonStyle}
                            onClick={() => {

                              handleFireBaseImage1Upload()
                              handleFireBaseImage2Upload()
                            }}
                        >
                            upload to firebase
                        </button>



        </div>
    )
}

export default UploadImage
