import React, { useState, useEffect } from 'react';
import ReactMarkdown from "react-markdown";
import MuiAlert from "@mui/material/Alert";
import { BrowserRouter as Router, useParams } from "react-router-dom";
import Snackbar from "@mui/material/Snackbar";
import { set } from "@firebase/database";
import axios from "axios"

import { makeStyles } from '@material-ui/core/styles'

const Post = (()=>{


  let { id } =
  useParams();


  const [post, setPost] = React.useState([]);
  const [title, setTitle] = useState("");
  const [desc, setDesc] = useState("");
  const [postId, setPostId] = React.useState("");
  useEffect(() => {
    const getPost = async () => {
      const posts = await axios.get("/posts/" + id);
      setPost(posts.data);
      setTitle(posts.data.title);
      setDesc(posts.data.desc);
      setPostId(posts.data._id)


      const comments = await axios.get(`/comment/${posts.data._id}`);
      console.log(comments)
      setComments(comments.data)

     };

    getPost();
  }, [id]);










    const [openError, setOpenError] = React.useState(false);

    const [error, setError] = React.useState("");
      const handleError = () => {
        setOpenError(!openError);
      };

    const Alert = React.forwardRef(function Alert(props, ref) {
        return <MuiAlert elevation={6} ref={ref} variant="filled" {...props} />;
      });

      const PF = "http://localhost:5000/images/";


      const useStyles = makeStyles((theme) => ({

        writeImg:{

          width: "100%",
          height: "250px",         
           borderRadius: "10px",
          objectFit: "cover",
          marginTop:"2%",
        },
        body:{

          width:"90%",
          marginLeft:"5%",


        },
        markdown:{

          width: "80vw",
          margin:'1px',


        },
        commentHeader:{
         display:'flex',
         flexDirection:"row", 
     backgroundColor:"#d6d6d4",



        },
        commentBody:{

          width: "80vw",
          margin:'1px',
          borderStyle:"solid",
          borderColor:"#d6d6d4",
          borderRadius:"15px",


        },
        commentSection:{



        },
        commentText:{

          margin:"1%",

        }


      }))

      const [comments, setComments] = useState([]);





      const [name, setName] = useState("");
      const [email, setEmail] = useState("");
      const [content, setContent] = useState(null);





      const handleSubmit = async (e) => {

        e.preventDefault();
        const newComment = {
          name,
          email,
          postId,
          content,
        };

        try {
          console.log("hey")
          const post = await axios.post("/comment", newComment);
          console.log("bingo")
          window.location.replace("/post/" + postId);
        } catch (err) {}
      };


      const classes = useStyles()
    return(
        <div className={classes.body}>
        <Snackbar
        open={openError}
        autoHideDuration={6000}
        onClose={handleError}
      >
        <Alert
          onClose={handleError}
          severity="success"
          sx={{ width: "100%" }}
        >

         {error}
        </Alert>
      </Snackbar>
      <div className={classes.body}>
      {post.photo && <img className={classes.writeImg} src={PF + post.photo} alt="" />}

        <h2 >{title}</h2>

        <ReactMarkdown className={classes.markdown}  >{desc}</ReactMarkdown>

        </div>
        <div className="write">



        <form className="writeForm" onSubmit={handleSubmit}>
          <div className="writeFormGroup">


            <input
              type="text"
              placeholder="Name"
              className="writeInput"
              autoFocus={true}
              onChange={e=>setName(e.target.value)}
            />



          </div>

          <div className="writeFormGroup">


          <input
            type="text"
            placeholder="Email"
            className="writeInput"
            autoFocus={true}
            onChange={e=>setEmail(e.target.value)}
          />



        </div>

          <div className="writeFormGroup">
            <textarea
              placeholder="Join the discussion"
              type="text"
              className="writeInput writeText"
              onChange={e=>setContent(e.target.value)}
            ></textarea>
          </div>
          <button className="writeSubmit" type="submit">
            Publish
          </button>
        </form>
      </div>
        <div >


        <div className={classes.body} >
        <h3>Comments</h3>
          {comments.map((comments) => (
            <div>
                 <div className={classes.commentBody}>
            <div className={classes.commentHeader}>
            <p>{comments.name}</p>
            <br/>
            <p>{new Date(comments.createdAt).toDateString()}</p>
            </div>

            <ReactMarkdown className={classes.commentText} >{comments.content}</ReactMarkdown>
          </div>

            </div>
        ))}

        </div>

          </div>

        </div>
    )})

    export default Post
