import React, { useRef } from "react";
import firebase from "firebase";
import { useEffect } from "react";
import { useCallback } from "react";
import Card from '@mui/material/Card';
import CardHeader from '@mui/material/CardHeader';
import CardMedia from '@mui/material/CardMedia';
import CardContent from '@mui/material/CardContent';
import Typography from '@mui/material/Typography';
import { CardActionArea } from '@mui/material';
import MuiAlert from "@mui/material/Alert";
import { storage } from '../index'
import { makeStyles } from '@material-ui/core/styles'
import Snackbar from "@mui/material/Snackbar";

import Button from '@material-ui/core/Button'



import Checkbox from '@material-ui/core/Checkbox'
import Select from '@material-ui/core/Select'
import InputLabel from '@material-ui/core/InputLabel'
import MenuItem from '@material-ui/core/MenuItem'
import Container from '@material-ui/core/Container'
import HeaderFile from './Header';
import { TextField } from '@material-ui/core'



import {
  Grid
} from '@material-ui/core/'
const BlogPosts = ({history})=>{


  const useStyles = makeStyles((theme) => ({

    card:{
       borderStyle:"solid",
       borderColor:"#b291f2",
       width:"100%",
       height:"100%",

    },
    cardHeader:{
    background: "#e2d7f7",
    color:"white"

   },
   blogs:{
    position: "absolute",

    left: "50%",
    transform: "translate(-50%, -50%)",
    width:"80%",

 },
 image:{
  position: "absolute",

  left: "50%",
  transform: "translate(-50%, -50%)",
  width:"100%",
  height:"80px",

},
  }))

  const classes = useStyles()

    const [blogPosts, setBlogPosts] = React.useState([]);


    function createBlogPost(
      title,
      date,
      readTime,
      author,
      image1Alt,
      preview,
      ) {
        return {
            title,
            date,
            readTime,
            author,
            image1Alt,
            preview,
        };
      }




      const [openError, setOpenError] = React.useState(false);
      const [error, setError] = React.useState("");
      const handleError = () => {
        setOpenError(!openError);
      };


      const Alert = React.forwardRef(function Alert(props, ref) {
        return <MuiAlert elevation={6} ref={ref} variant="filled" {...props} />;
      });

    const handleGetPosts = useCallback(() => {
        let items = [];

        firebase
          .firestore()
          .collection("Posts")
          .get()
          .then(
            (p) => {
              p.forEach((doc) => {
                let blogInformation = doc.data();
          console.log(blogInformation)
                items.push(
                  createBlogPost(
                    blogInformation.title,
                    blogInformation.date,
                    blogInformation.readTime,
                    blogInformation.author,
                    blogInformation.image1Alt,
                    blogInformation.preview,


                  )
                );

              });
              console.log(items)
              setBlogPosts(items);
              console.log(blogPosts)



            },
            [setBlogPosts]
          )
          .catch((error) => {
            console.error("Error retrieving document: ", error);
            setError(error.message);
            handleError()
          });
      }, []);


      useEffect(() => {

        handleGetPosts()
        console.log(blogPosts)
              }, []);




    return(
        <div>


        <Snackbar
        open={openError}
        autoHideDuration={6000}
        onClose={handleError}
      >
        <Alert
          onClose={handleError}
          severity="success"
          sx={{ width: "100%" }}
        >
         {error}
        </Alert>
      </Snackbar>
<div className={classes.blogs}>
      <Grid
      container
      spacing={1}
      direction="row"
      justify="center"
      alignItems="flex-start"

  >

  {blogPosts.map((blogPosts) => (
    <Grid item xs={12} sm={4} md={3} >
    <Card 
    className={classes.card}
    onClick={() => {

      history.push(`post/${blogPosts.title}`)

  }}
    sx={{ maxWidth: 345 }}>
    <CardActionArea>
    <CardHeader
    key={blogPosts.title}
    className={classes.cardHeader}
    title={blogPosts.title}
    subheader={`${blogPosts.date} - ${blogPosts.readTime}`}
  />


    </CardActionArea>
  </Card>
  </Grid>

))}

  </Grid>

  </div>
        </div>
    )}

    export default BlogPosts
