import React, { useState, useEffect } from 'react';
import blogPost from '../Data/blog.json';
import firebase from "firebase";
import { useCallback } from "react";

import MuiAlert from "@mui/material/Alert";
import { BrowserRouter as Router, useParams } from "react-router-dom";
import Snackbar from "@mui/material/Snackbar";
import { set } from "@firebase/database";
import HeaderFile from './Header';
import { storage } from '../index'
import axios from "axios"

const Post = (()=>{

  useEffect(() => {
    console.log("hey")
    const res =  axios.get("/posts/");
    console.log(res)
    setPost(res.data);
    console.log(title)

  },[title, desc]);



    const [post, setPost] = useState({
      id: "" ,
      blogCategory: "" ,
      blogTitle : "" ,
      postedOn: "" ,
      author: "" ,
      blogImage: "" ,
      blogText: ""
  });
  const [slug, setSlug] = useState('');




    let { id } =
    useParams();

    const [title, setTitle] = React.useState("");
    const [date, setDate] = React.useState("");
    const [readTime, setReadTime] = React.useState("");
    const [author, setAuthor] = React.useState("");
    const [desc, setDesc] = React.useState("");
    const [image1, setImage1] = React.useState("");
    const [image2, setImage2] = React.useState("");
    const [image1Alt, setImage1Alt] = React.useState("");
    const [image2Alt, setImage2Alt] = React.useState("");




    const [openError, setOpenError] = React.useState(false);

    const [error, setError] = React.useState("");
      const handleError = () => {
        setOpenError(!openError);
      };

    const Alert = React.forwardRef(function Alert(props, ref) {
        return <MuiAlert elevation={6} ref={ref} variant="filled" {...props} />;
      });


      const uploadData = () => {
        firebase.firestore().collection("cities").doc("LA").set({
            name: "Los Angeles",
            state: "CA",
            country: "USA"
        })
        .then(() => {
            console.log("Document successfully written!");
        })
        .catch((error) => {
            console.error("Error writing document: ", error);

        })}



    const handleGetPost = useCallback(() => {
console.log(id)

      firebase.firestore()
          .collection("Posts")
          .doc(id)
          .get()
          .then(
            (doc) => {
              let postInformation = doc.data();

                 setTitle(postInformation.title)
                 console.log(postInformation.title)
                 setDate(postInformation.date)
                 setReadTime(postInformation.readTime)
                 setAuthor(postInformation.author)
                 setDesc(postInformation.paragraph)
setImage1(postInformation.image1)
setImage2(postInformation.image2)
setImage2Alt(postInformation.image2Alt)
setImage1Alt(postInformation.setImage1Alt)




              })
              .catch((error) => {
            console.error("Error retrieving document: ", error);
            setError(error.message);
            handleError();
          });
      }, []);




    return(
        <div>
        <Snackbar
        open={openError}
        autoHideDuration={6000}
        onClose={handleError}
      >
        <Alert
          onClose={handleError}
          severity="success"
          sx={{ width: "100%" }}
        >

         {error}
        </Alert>
      </Snackbar>
      <HeaderFile></HeaderFile>

      <p>hey</p>
        <h2>{title}</h2>
        <p>{author}</p>
        <p>{new Date (post.createdAt).toDateString()}</p>


        <img alt={image1Alt} url={image1}></img>
        <img alt={image2Alt} url={image2}></img>

        <button

        onClick={() => {

            // handleFireBaseUpload()
            uploadData()


        }}
    >
        Submit
    </button>

        </div>
    )})

    export default Post
