import React, { useState } from 'react'
import { slide as Menu } from 'react-burger-menu'
import { makeStyles } from '@material-ui/core/styles'
import Profile from '../Images/profilePic.JPG';
import Snackbar from "@mui/material/Snackbar";
import { Link } from "react-router-dom";
import MuiAlert from "@mui/material/Alert";
   import Blog from './Blog'
   import firebase from "firebase";
   import { useEffect } from "react";
   import { useCallback } from "react";
   import Card from '@mui/material/Card';
   import CardHeader from '@mui/material/CardHeader';
   import { CardActionArea } from '@mui/material';
   import { storage } from '../index'
   import axios from "axios"
   import {
       Grid
     } from '@material-ui/core/'

const SideBar = (({history})=>{

    const useStyles = makeStyles((theme) => ({


container:{
  borderRadius: "25px",
  borderColor:"black",
  borderWidth:'1px',
  borderStyle:"solid",
  padding: "20px",
  width: "20vw",
  height: "450px",
  margin:"10px",

  },


  subscribe:{

    width:"100%",

    background: "#e2d7f7",
    padding:"5px",
    borderRadius: "25px",

 },
 about:{


  marginLeft:"25%",
  marginRight:'25%',
  width:"100%",
padding:'1px',

},

 subscribeButton:{


  background: "#ac82fa",
borderStyle:"none",
borderRadius:"25px",
height:'30px',


},


aboutButton:{


  background: "#ac82fa",
borderStyle:"none",
borderRadius:"25px",
height:'30px',
marginLeft:"25%",
marginRight:'25%',
    display:"flex",
    justifyContent:"center",
    padding:"10px",

},


 categories:{
borderWidth:"1px",
borderLeftColor:"white",
borderRightColor:"white",
borderStyle:"solid",
padding:"2px",

},

image:{

    width: "100px",
    height: "100px",
    borderRadius:"50px",
    display:"flex",
    justifyContent:"center",
    marginLeft:"25%",
    marginRight:'25%',



},

    }))


    const [posts, setPosts] = useState([]);
    const [categories, setCategories] = useState(["My Experience", "Cool Ideas", "Thoughts"]);
    const PF = "http://localhost:5000/images/";


    useEffect(() => {
      const fetchPosts = async () => {
       const res = await axios.get("/posts");
       console.log(res)
       setPosts(res.data)

      };
      fetchPosts();

    }, []);



    const [openSubscribeSuccess, setOpenSubscribeSuccess] = React.useState(false);

    const [subscribeSucessfull, setSubscribeSuccessfull] = React.useState("");
      const handleSubscribeSuccess = () => {
        setSubscribeSuccessfull(!openSubscribeSuccess);
      };

    const Alert = React.forwardRef(function Alert(props, ref) {
        return <MuiAlert elevation={6} ref={ref} variant="filled" {...props} />;
      });

    const [name, setName] = useState("");
    const [email, setEmail] = useState("");


    const handleSubmit = async (e) => {

      const newSubscription = {
        name,
        email,
      };
      try {
        await axios.post("/subscribe", newSubscription);
        handleSubscribeSuccess()
        setName("")
        setEmail("")
      } catch (err) {}
    };


    const classes = useStyles()
    return(



<div className={classes.container}>
<Snackbar
open={subscribeSucessfull}
autoHideDuration={6000}
onClose={handleSubscribeSuccess}
>
<Alert
  onClose={handleSubscribeSuccess}
  severity="success"
  sx={{ width: "100%" }}
>

 {"Subscribed Successfully!"}
</Alert>
</Snackbar>

<img className={classes.image} src={Profile} alt=""></img>
<p className={classes.about} >HI, I'M ELLA</p>
<button    className={classes.aboutButton} >About Me!</button>
     <p className={classes.categories}>Categories</p>

     <ul listStyle = "none">
{categories.map((categories) => (
    <li  ><Link style={{ textDecoration: 'none' }}  className="link" to={`/filter/${categories}`} >{categories}</Link></li>
))}
</ul>

     <div className={classes.subscribe}>
     <p>Subscribe</p>
     <p>Enter your email to subscribe:</p>
     <input
     type="text"
     placeholder="Name"

     autoFocus={true}
     onChange={e=>setName(e.target.value)}
   />
     <input
     type="text"
     placeholder="Email Address"

     autoFocus={true}
     onChange={e=>setEmail(e.target.value)}
   />
     <button    onClick={() => {

      handleSubmit()
     }}

     className={classes.subscribeButton}>

     Subscribe!</button>
     </div>

  </div>

    )
})

export default SideBar
