import React, { useRef } from "react";
import firebase from "firebase";
import { useEffect } from "react";
import { useCallback } from "react";
import Card from '@mui/material/Card';
import CardHeader from '@mui/material/CardHeader';
import CardMedia from '@mui/material/CardMedia';
import CardContent from '@mui/material/CardContent';
import Typography from '@mui/material/Typography';
import { CardActionArea } from '@mui/material';
import MuiAlert from "@mui/material/Alert";
import { storage } from '../index'

import Snackbar from "@mui/material/Snackbar";
const BlogPosts = (()=>{

    const UrlInfo = (props) => {
        const [url, setUrl] = React.useState('')
    var gsReference = storage.refFromURL(`gs://techinwithella.appspot.com/images/${props.name}`)
    gsReference.getDownloadURL().then(function(url) {
        setUrl(url)
        console.log(url);
    })
    return(




        <div>
        <img src= {url} alt="Hey" width="100px" height="100px"/>
        </div>)}

    const [blogPosts, setBlogPosts] = React.useState([]);
    function createBlogPost(
     title,
     date,
     readTime,
     author,
     image1,
     image1Alt,
     preview,
      ) {
        return {
            title,
            date,
            readTime,
            author,
            image1,
            image1Alt,
            preview,
        };
      }




      const [openError, setOpenError] = React.useState(false);
      const [error, setError] = React.useState("");
      const handleError = () => {
        setOpenError(!openError);
      };


      const Alert = React.forwardRef(function Alert(props, ref) {
        return <MuiAlert elevation={6} ref={ref} variant="filled" {...props} />;
      });

    const handleGetPosts = useCallback(() => {
        let items = [];

        firebase
          .firestore()
          .collection("Posts")
          .get()
          .then(
            (p) => {
              p.forEach((doc) => {
                let blogInformation = doc.data();
          console.log(blogInformation)
                items.push(
                  createBlogPost(
                    blogInformation.title,
                    blogInformation.date,
                    blogInformation.readTime,
                    blogInformation.author,
                    blogInformation.paragraph,
                    blogInformation.image1,
                    blogInformation.image1Alt,
                    blogInformation.preview,



                  )
                );
              });

              setBlogPosts(items);


            },
            [setBlogPosts]
          )
          .catch((error) => {
            console.error("Error retrieving document: ", error);
            setError(error.message);
            handleError()
          });
      }, []);

    return(
        <div>
        <h2>hey</h2>
        <Snackbar
        open={openError}
        autoHideDuration={6000}
        onClose={handleError}
      >
        <Alert
          onClose={handleError}
          severity="success"
          sx={{ width: "100%" }}
        >
         {error}
        </Alert>
      </Snackbar>

        {blogPosts.map((blogPosts) => (
            <Card sx={{ maxWidth: 345 }}>
            <CardActionArea>
            <CardHeader
            title={blogPosts.title}
            subheader={blogPosts.date}
          />
          <CardMedia
          component="img"
          height="194"
          image="/static/images/cards/paella.jpg"
          alt={blogPosts.image1Alt}
        />
        <CardContent>
          <Typography variant="body2" color="text.secondary">
        {blogPosts.preview}
          </Typography>
        </CardContent>
            </CardActionArea>
          </Card>

        ))}


        </div>
    )})

    export default BlogPosts
